    private void jButton2MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_jButton2MouseClicked
        // EDITAR
        UsuariosDatos.usuarioLoggedIn.setUsername(jTextField_username.getText());
        UsuariosDatos.usuarioLoggedIn.setPass(jTextField_password.getText());
        UsuariosDatos.usuarioLoggedIn.setNombre(jTextField_nombre.getText());
        jLabel_anuncios.setText("Se ha editado correctamente");
    
        
    }//GEN-LAST:event_jButton2MouseClicked

